color_palette.css_variables:
:root {
  /* Primary Port Wine Colors */
  --color-primary: #6B2C3D;
  --color-primary-light: #8B4555;
  --color-primary-dark: #4A1F2A;
  --color-primary-rgb: 107, 44, 61;

  /* Secondary Gold Accents */
  --color-secondary: #C5A572;
  --color-secondary-light: #D4B891;
  --color-secondary-dark: #A88A5E;

  /* Rose Highlight */
  --color-highlight: #B76E79;
  --color-highlight-light: #D4909C;

  /* Backgrounds - Warm Cream */
  --color-bg: #FAF8F5;
  --color-bg-dark: #F5F1ED;
  --color-bg-white: #FFFFFF;

  /* Text - Rich Charcoal */
  --color-text: #2C2C2C;
  --color-text-light: #666666;
  --color-text-muted: #999999;

  /* Semantic Colors */
  --color-success: #6B7C3D;
  --color-warning: #C59B72;
  --color-error: #A64253;

  /* Luxury Overlays */
  --overlay-light: rgba(107, 44, 61, 0.08);
  --overlay-medium: rgba(107, 44, 61, 0.16);
  --overlay-dark: rgba(107, 44, 61, 0.32);

  /* Gradients */
  --gradient-primary: linear-gradient(135deg, #6B2C3D 0%, #8B4555 100%);
  --gradient-luxury: linear-gradient(135deg, #C5A572 0%, #B76E79 100%);
  --gradient-shimmer: linear-gradient(90deg, transparent, rgba(197, 165, 114, 0.3), transparent);
}
